import { useState } from "react";

export const loans = [
  {
    id: 1,
    payeeID: 54321,
    originatorID: 99887,
    totalAmount: 1245.67,
    isActive: true
  },
  {
    id: 2,
    payeeID: 11221,
    originatorID: 99887,
    totalAmount: 45.22,
    isActive: true
  },
  {
    id: 3,
    payeeID: 54321,
    originatorID: 43437,
    totalAmount: 125.02,
    isActive: true
  },
  {
    id: 4,
    payeeID: 87654,
    originatorID: 60613,
    totalAmount: 5.77,
    isActive: true
  },
  {
    id: 5,
    payeeID: null,
    originatorID: 99887,
    totalAmount: 12345.67,
    isActive: false
  }
];

const useAPICall = (
  isBroken = false
): { data: any; loading: boolean; error: any } => {
  const [data, setData] = useState<any>(null);
  if (!isBroken && !data) {
    setTimeout(() => {
      setData({
        loans
      });
    }, 500);
  } else {
    if (!data) {
      setTimeout(() => {
        setData({
          errors: [
            {
              message: "Could not load loans",
              code: "SVC_EXEC_123"
            }
          ]
        });
      }, 500);
    }
  }
  return {
    data: isBroken ? null : data,
    loading: Boolean(!data),
    error: isBroken ? data : null
  };
};

export default useAPICall;
