import uuid from 'uuid';
const rootResolver = {
    loans: () => {
      return Array.from({length: 100}).map((_, index) => {
        const seed = Math.random();
        const isPartnerLoan = index % 13 === 0;
        return {
            id: btoa(`Loan:${uuid.v4()}`),
            amount: Math.floor(seed * (index + 1) * 1000),
            interestRate: (Math.floor(seed * 100000) % 8) / 100,
            partnerId: isPartnerLoan ? uuid.v4() : undefined,
        }
      });
    },
  };

export default rootResolver;
